package oct.ex_11102024.Task;public class BaseTestAPI {    private String name;    private String repoName;    private int id;    public BaseTestAPI() {        System.out.println("DC - BaseClass");    }    public BaseTestAPI(String b) {        System.out.println("PC - BaseClass");    }    public String getName() {        return name;    }    public void setName(String name, boolean isAuth) {        if (isAuth) {            this.name = name;        } else {            System.out.println("Not Allowed");        }    }    public String getRepoName() {        return repoName;    }    public void setRepoName(String repoName, boolean isAuth) {        if (isAuth) {            this.repoName = repoName;        } else {            System.out.println("Not Allowed");        }    }    public int getId() {        return id;    }    public void setId(int id, boolean isAuth) {        if (isAuth) {            this.id = id;        } else {            System.out.println("Not Allowed");        }    }    void performHTTPMethod() {        System.out.println("Perform HTTP Method");    }    void performHTTPMethod(String methodName) {        System.out.println("Perform HTTP Method Call " + methodName);    }    void performGet() {        System.out.println("Perform GET Call");    }    void performPOST() {        System.out.println("Perform POST Call");    }    void performPATCH() {        System.out.println("Perform PATCH Call");    }    void performPUT() {        System.out.println("Perform PUT Call");    }    void performDELETE() {        System.out.println("Perform DELETE Call");    }}